import { getpmedianproject, addpmedianproject, amendpmedianproject, getplotlocation } from '@/api/model'
export default {
    data(){
        return{
            table_loading: true,
            tablekey: 0,
            tableData: [],
            page_data: [],
            formData: [],
            total_size: 0,
            currentPage: 1,
            page_size: 10,
            amend_dialog: false,
            get_dialog: false,
            add_dialog: false,
            LocationImage: false,
            form: {},
            get_form: {
                project_id: '',
                plot_num: ''
            },
            url: '',
            image_loading: false
        }
    },
    methods: {
        getData(){
            let that = this
            getpmedianproject().then(res=>{
                if (res.code === 20000){
                    that.table_loading = false
                    that.tableData = res.data
                    let size = that.page_size
                    let index = that.currentPage-1
                    that.page_data = []
                    for (let i=index*size; i<(index+1)*size; i++){
                        if (i==res.data.length){
                            break
                        }
                        that.page_data.push(res.data[i])
                    }
                    that.total_size = res.data.length
                }
            })
        },
        AmendData(index){
           this.form = this.page_data[index]
           this.amend_dialog = true
        },
        AmendDataConfirm(){
            let data = this.form
            let that = this
            amendpmedianproject(data).then(res=>{
                if (res.code === 20000){
                    this.$message({
                        type: 'success',
                        message: '修改成功'
                    })
                    that.page_data = []
                    that.getData()
                }
            })
            this.amend_dialog = false
        },
        GetLocation(index){
        if (this.page_data[index].basic_size === 0 || this.page_data[index].ts_size === 0 || this.page_data[index].rrc_size === 0 ) {
          this.$message.error('请先导入实验参数表')
        } else {
            this.get_form.project_id = this.page_data[index].project_id
            let that = this
            that.page_data[index].project_state = '正在运行'
            getplotlocation(this.get_form).then(res=>{
                if (res.code === 20000){
                    this.$message({
                        type: 'success',
                        message: 'success'
                    })
                }
                that.getData()
            })

        }
        },
        GetLocationConfirm(){
            let that = this
            getplotlocation(this.get_form).then(res=>{
                if (res.code === 20000){
                    this.$message({
                        type: 'success',
                        message: 'success'
                    })
                }
            })
        },
        addData(){
          this.add_dialog = true
        },
        addDataConfirm(){
            let data = this.form
            let that = this
            addpmedianproject(data).then(res=>{
                if (res.code === 20000){
                    this.$message({
                        type: 'success',
                        message: '添加成功'
                    })
                    that.page_data = []
                    that.getData()
                }
            })
            this.add_dialog = false
        },
        handleSizeChange(val){
            this.table_loading = true
            this.page_size = val
            this.currentPage = 1
            this.page_data = []
            this.getData()
        },
        handleCurrentChange(val){
            this.currentPage = val
            this.page_data = []
            this.getData()
        }
    },
    mounted(){
        this.getData()
    }
}
