import { getkmeansrpoject, addkmeansproject, amendkmeansproject, startkmeans } from '@/api/model'
export default {
    data(){
        return{
            table_loading: true,
            tablekey: 0,
            tableData: [],
            page_data: [],
            formData: [],
            total_size: 0,
            currentPage: 1,
            page_size: 10,
            amend_dialog: false,
            get_dialog: false,
            add_dialog: false,
            LocationImage: false,
            form: {},
            add_form: {
                project_id: '',
                name: ''
            },
            get_form: {
                project_id: '',
                plot_num: ''
            },
            url: '',
            image_loading: false,
            choose_dialog: false,
            chooseIndex: null,
            choose_data: ['MSW','POP','PUP','HOU','APH','GEN','AGE1','AGE2','AGE3','INC','EXP','BUD','GDP','GDP1','GDP2','GDP3','pGDP','EDU'],
            choose_list: [],
        }
    },
    methods: {
        Refresh:function(){
            this.table_loading = true
            this.page_data = []
            this.tableData = []
            this.getData()
        },
        Select_Index:function(arr_main, arr_child){
            let index = []
            let flag = true
            for (let i=0; i<arr_main.length; i++){
                flag = true
                for (let j=0; j<arr_child.length; j++){
                    if (arr_main[i]===arr_child[j]){
                        flag=false
                        break
                    }
                }
                if (flag){
                    index.push(i)
                }
            }
            return index;
        },
        ChooseConfirm:function(){
            if (this.choose_list.length != 2){
                this.$message.error('只能选择两个维度')
            }
            else{
                this.choose_dialog = false
                let val = this.chooseIndex
                let that = this
                let data = {}
                let index_list = []
                data['project_id'] = this.page_data[val].project_id
                index_list = this.Select_Index(this.choose_data, this.choose_list)
                data['index_list'] = index_list.toString()
                startkmeans(data).then(res=>{
                    if (res.code === 20000){
                        this.$message({
                            type: 'success',
                            message: '运行成功'
                        })
                        that.table_loading = true
                        that.page_data = []
                        that.tableData = []
                        that.getData()
                    }
                })
            }
        },
        Experiment:function(val){
            this.chooseIndex = val
            this.choose_dialog = true
        },
        timeStamptoTime:function(time){
            var date = new Date(time);
            let Y = date.getFullYear() + '-';
            let M = (date.getMonth()+1 < 10 ? '0'+(date.getMonth()+1) : date.getMonth()+1) + '-';
            let D = date.getDate() + ' ';
            let h = date.getHours() + ':';
            let m = date.getMinutes() + ':';
            let s = date.getSeconds();
            return Y+M+D+h+m+s;
        },
        getData(){
            let that = this
            getkmeansrpoject().then(res=>{
                if (res.code === 20000){
                    that.table_loading = false
                    that.tableData = res.data
                    for (let i=0; i<res.data.length; i++){
                        let time = res.data[i].add_time
                        time = new Date(time.replace(/-/g,'/')).getTime()+3600*1000*8
                        res.data[i].add_time = that.timeStamptoTime(time)
                    }
                    let size = that.page_size
                    let index = that.currentPage-1
                    for (let i=index*size; i<(index+1)*size; i++){
                        if (i==res.data.length){
                            break
                        }
                        that.page_data.push(res.data[i])
                    }
                    that.total_size = res.data.length
                }
            })
        },
        AmendData(index){
           this.form = this.page_data[index]
           this.amend_dialog = true
           
        },
        AmendDataConfirm(){
            let data = this.form
            let that = this
            amendkmeansproject(data).then(res=>{
                if (res.code === 20000){
                    this.$message({
                        type: 'success',
                        message: '修改成功'
                    })
                    that.page_data = []
                    that.getData()
                }
            })
            this.amend_dialog = false
        },
        Project_start(index){
        if (this.page_data[index].lstm_project_table_size === 0) {
          this.$message.error('请先导入实验参数表')
        } else {
            let that = this
            LstmProjectStart(this.page_data[index].project_id).then(res=>{
              if(res.code === 20000) {
                this.$message({
                  type: 'success',
                  message: '模型开始运行'
                })
              } else if (res.code === 50000) {
                this.$message({
                  type: 'error',
                  message: '模型已在运行中！'
                })
              }
            }
            )
        }
        },
        addPrograme(){
          this.add_dialog = true
        },
        addDataConfirm(){
            let data = this.add_form
            if (this.add_form.project_id === ''){
                this.$message.error('请输入项目ID')
            }
            else if (this.add_form.name === ''){
                this.$message.error('请输入项目名称')
            }
            else{
                 let that = this
                addkmeansproject(data).then(res=>{
                    if (res.code === 20000){
                        this.$message({
                            type: 'success',
                            message: '添加成功'
                        })
                        that.page_data = []
                        that.getData()
                    }
                })
                this.add_dialog = false
            }
        },
        handleSizeChange(val){
            this.table_loading = true
            this.page_size = val
            this.currentPage = 1
            this.page_data = []
            this.getData()
        },
        handleCurrentChange(val){
            this.currentPage = val
            this.page_data = []
            this.getData()
        }
    },
    mounted(){
        this.getData()
    }
}
