import { getrelationproject, addrelationproject, amendrelationproject, startrelation } from '@/api/model'
export default {
    data(){
        return{
            table_loading: true,
            tablekey: 0,
            tableData: [],
            page_data: [],
            formData: [],
            total_size: 0,
            currentPage: 1,
            page_size: 10,
            amend_dialog: false,
            get_dialog: false,
            add_dialog: false,
            choose_dialog: false,
            choose_value: null,
            choose_item: [
                { value: '1', label: '相关矩阵'},
                { value: '2', label: '随机森林'},
                { value: '3', label: '灰度分析'},
                { value: '4', label: '皮尔逊系数'}
            ],
            choose_id: null,
            LocationImage: false,
            form: {},
            add_form: {
                project_id: '',
                name: ''
            },
            get_form: {
                project_id: '',
                plot_num: ''
            },
            url: '',
            image_loading: false
        }
    },
    methods: {
        Refresh:function(){
            this.table_loading = true
            this.page_data = []
            this.tableData = []
            this.getData()
        },
        Experiment:function(val){
            this.choose_dialog = true
            this.choose_id = this.page_data[val].project_id
        },
        ExperimentConfirm:function(){
            let that = this
            if (this.choose_value === null){
                this.$message.error('请选择具体算法')
            }
            else{
                let data = {}
                data['project_id'] = this.choose_id
                data['algorithm'] = this.choose_value
                startrelation(data).then(res=>{
                    that.choose_dialog = false
                    if (res.code === 20000){
                        that.$message({
                            type: 'success',
                            message: '运行成功'
                        })
                        that.table_loading = true
                        that.page_data = []
                        that.tableData = []
                        that.getData()
                    }
                })
            }
        },
        timeStamptoTime:function(time){
            var date = new Date(time);
            let Y = date.getFullYear() + '-';
            let M = (date.getMonth()+1 < 10 ? '0'+(date.getMonth()+1) : date.getMonth()+1) + '-';
            let D = date.getDate() + ' ';
            let h = date.getHours() + ':';
            let m = date.getMinutes() + ':';
            let s = date.getSeconds();
            return Y+M+D+h+m+s;
        },
        getData(){
            let that = this
            getrelationproject().then(res=>{
                if (res.code === 20000){
                    that.table_loading = false
                    that.tableData = res.data
                    for (let i=0; i<res.data.length; i++){
                        let time = res.data[i].add_time
                        time = new Date(time.replace(/-/g,'/')).getTime()+3600*1000*8
                        res.data[i].add_time = that.timeStamptoTime(time)
                    }
                    let size = that.page_size
                    let index = that.currentPage-1
                    for (let i=index*size; i<(index+1)*size; i++){
                        if (i==res.data.length){
                            break
                        }
                        that.page_data.push(res.data[i])
                    }
                    that.total_size = res.data.length
                }
            })
        },
        AmendData(index){
           this.form = this.page_data[index]
           this.amend_dialog = true
           
        },
        AmendDataConfirm(){
            let data = this.form
            let that = this
            amendrelationproject(data).then(res=>{
                if (res.code === 20000){
                    this.$message({
                        type: 'success',
                        message: '修改成功'
                    })
                    that.page_data = []
                    that.getData()
                }
            })
            this.amend_dialog = false
        },
        Project_start(index){
        if (this.page_data[index].lstm_project_table_size === 0) {
          this.$message.error('请先导入实验参数表')
        } else {
            let that = this
            LstmProjectStart(this.page_data[index].project_id).then(res=>{
              if(res.code === 20000) {
                this.$message({
                  type: 'success',
                  message: '模型开始运行'
                })
              } else if (res.code === 50000) {
                this.$message({
                  type: 'error',
                  message: '模型已在运行中！'
                })
              }
            }
            )
        }
        },
        addPrograme(){
          this.add_dialog = true
        },
        addDataConfirm(){
            let data = this.add_form
            if (this.add_form.project_id === ''){
                this.$message.error('请输入项目ID')
            }
            else if (this.add_form.name === ''){
                this.$message.error('请输入项目名称')
            }
            else{
                 let that = this
                addrelationproject(data).then(res=>{
                    if (res.code === 20000){
                        this.$message({
                            type: 'success',
                            message: '添加成功'
                        })
                        that.page_data = []
                        that.getData()
                    }
                })
                this.add_dialog = false
            }
        },
        handleSizeChange(val){
            this.table_loading = true
            this.page_size = val
            this.currentPage = 1
            this.page_data = []
            this.getData()
        },
        handleCurrentChange(val){
            this.currentPage = val
            this.page_data = []
            this.getData()
        }
    },
    mounted(){
        this.getData()
    }
}
