import { getregressionmodelfile, getregression, getexcelinfo, startregression, addregression, amendregression } from "@/api/model";
export default {
  data() {
    return {
      tableshow: true,
      table_loading: false,
      tablekey: 0,
      tableData: [],
      page_data: [],
      total_size: 0,
      currentPage: 1,
      page_size: 10,
      fileData: [],
      choose_data_dialog: false,
      selectdata: null,
      selected_project_index: null,
      selectfilePath: null,
      select_cols: null,
      select_cols_num: null,
      choose_col_dialog: false,
      reference_col: null,
      checkList: [],
      add_form: {},
      add_dialog: false,
      form: {},
      amend_dialog: false,
      formula_dim: null
    };
  },
  watch: {
    selectdata(val) {
      let that = this;
      let data = {};
      data["url"] = this.fileData[val].path;
      this.selectfilePath = this.fileData[val].path;
      getexcelinfo(data).then((res) => {
        if (res.code === 20000) {
          that.select_cols = res.cols;
          that.select_cols_num = res.col_num;
        }
      });
    },
  },
  methods: {
    isInArray(arr, item){
      for (let i=0; i<arr.length; i++){
        if (arr[i] === item){
          return true
        }
      }
      return false
    },
    AmendData(index){
        this.form = this.page_data[index]
        this.amend_dialog = true
    },
    AmendDataConfirm:function(){
      let that = this
      amendregression(this.form).then(res=>{
        if (res.code === 20000){
          this.$message({
            type: 'success',
            message: '修改成功'
          })
          that.amend_dialog = false
          that.table_loading = true;
          that.page_data = [];
          that.tableData = [];
          that.getData();
        }
      })
    },
    addPrograme:function(){
      this.add_dialog = true
    },
    addDataConfirm:function(){
      let data = this.add_form
      if (this.add_form.project_id === ''){
          this.$message.error('请输入项目ID')
      }
      else if (this.add_form.name === ''){
          this.$message.error('请输入项目名称')
      }
      else{
        let that = this
        addregression(this.add_form).then(res=>{
          if (res.code === 20000){
            this.$message({
              type: 'success',
              message: '添加成功'
            })
            that.add_dialog = false
            that.table_loading = true;
            that.page_data = [];
            that.tableData = [];
            that.getData();
          }
        })
      }
    },
    Select_Index: function (arr_main, arr_child) {
      let index = [];
      let flag = true;
      for (let i = 0; i < arr_main.length; i++) {
        flag = true;
        for (let j = 0; j < arr_child.length; j++) {
          if (arr_main[i] === arr_child[j]) {
            flag = false;
            break;
          }
        }
        if (flag) {
          index.push(i);
        }
      }
      return index;
    },
    getIndex:function(arr, item){
        for (let i=0; i<arr.length; i++){
            if (arr[i] === item){
                return i
            }
        }
    },
    getCookie:function(name){
        var strcookie = document.cookie;
        var arrcookie = strcookie.split("; ");
        for ( var i = 0; i < arrcookie.length; i++) {
            var arr = arrcookie[i].split("=");
            if (arr[0] == name){
                return arr[1];
            }
        }
        return "";
    },
    startExperiment:function(){
        let that = this
        if (this.reference_col === null){
            this.$message.error('请选择预测指标')
        }
        else if (this.checkList.length === 0){
            this.$message.error('请选择参考指标')
        }
        else if (this.isInArray(this.checkList, this.reference_col)){
          this.$message.error('参考指标不能和试验指标重叠')
        }
        else if (this.formula_dim === null){
          this.$message.error('请输入公式高次方项')
        }
        else if (this.formula_dim <= 0){
          this.$message.error('输入高次方必须为正')
        }
        else{
            this.choose_col_dialog = false
            this.choose_data_dialog = false
            let data = {}
            data['project_id'] = this.page_data[this.selected_project_index].project_id
            let index_list = this.Select_Index(this.select_cols, this.checkList)
            data['drop_col'] = index_list
            data['special'] = this.getIndex(this.select_cols, this.reference_col)
            data['path'] = this.selectfilePath
            data['name'] = this.getCookie('environment_name')
            data['dim'] = this.formula_dim
            startregression(data).then(res=>{
              if (res.code === 20000){
                  this.$message({
                      type: 'success',
                      message: '运行成功'
                  })
                  that.table_loading = true;
                  that.page_data = [];
                  that.tableData = [];
                  that.getData();
                      }
                  })
        }
    },
    chooseData:function(){
        this.choose_col_dialog = true
    },
    Refresh: function () {
      this.table_loading = true;
      this.page_data = [];
      this.tableData = [];
      this.getData()
    },
    Experiment: function (val) {
      this.selected_project_index = val;
      this.choose_data_dialog = true;
    },
    getData: function () {
      let that = this;
      getregression().then((res) => {
        if (res.code === 20000) {
          that.table_loading = false;
          that.tableData = res.data;
          let size = that.page_size;
          let index = that.currentPage - 1;
          for (let i = index * size; i < (index + 1) * size; i++) {
            if (i == res.data.length) {
              break;
            }
            res.data[i].tag = "未选择";
            that.page_data.push(res.data[i]);
          }
          that.total_size = res.data.length;
        }
      });
    },
    getFileList: function () {
      let that = this;
      this.fileData = [];
      getregressionmodelfile().then((res) => {
        if (res.code === 20000) {
          let data = res.data;
          for (let i = 0; i < data.length; i++) {
            let dict = {};
            dict["index"] = i;
            dict["name"] = data[i].name;
            dict["path"] = data[i].url;
            that.fileData.push(dict);
          }
        }
      });
    },
    handleSizeChange(val) {
      this.table_loading = true;
      this.page_size = val;
      this.currentPage = 1;
      this.page_data = [];
      this.getData();
    },
    handleCurrentChange(val) {
      this.currentPage = val;
      this.page_data = [];
      this.getData();
    },
  },
  mounted() {
    this.getData();
    this.getFileList();
  },
};
