import echarts from 'echarts'
require('echarts/theme/westeros') // echarts theme
import resize from './mixins/resize'
export default {
    name: "household",
    mixins:[resize],
    props:{
        className: {
            type: String,
            default: 'chart'
        },
        width: {
            type: String,
            default: '100%'
        },
        height: {
            type: String,
            default: '250px'
        },
        autoResize: {
            type: Boolean,
            default: true
        },
        chartData: {
            type: Object,
            required: true
        }
    },
    data(){
        return {
            chart: null
        }
    },
    watch: {
        chartData: {
            deep: true,
            handler(val) {
                this.setOptions(val)
            }
        }
    },
    mounted() {
        this.$nextTick(() => {
            this.initChart()
        })
    },
    beforeDestroy() {
        if (!this.chart) {
            return
        }
        this.chart.dispose();
        this.chart = null
    },
    methods: {
        initChart(){
            this.chart = echarts.init(this.$el, 'westeros');
            this.setOptions(this.chartData)
        },
        setOptions(val){
            let household = val.households
            let year = val.year
            this.chart.setOption({
                title: {
                    text: '上海市户籍户数'
                },
                tooltip: {
                    trigger: 'axis',
                    axisPointer: {
                        type: 'cross',
                        label: {
                            backgroundColor: '#6a7985'
                        }
                    }
                },
                toolbox: {
                    feature: {
                        saveAsImage: {}
                    }
                },
                grid: {

                    containLabel: true
                },
                xAxis: [
                    {
                        type: 'category',
                        boundaryGap: false,
                        data: year
                    }
                ],
                yAxis: [
                    {
                        type: 'value',
                        name: '万户'
                    }
                ],
                series: [
                    {
                        name: '户数',
                        type: 'line',
                        stack: '总量',
                        data: household
                    },
                ]
            })
        }
    }
}
