result:
.header{
    width: 100%;
    height: 70px;
    background: #fff;
    box-shadow: 0 0 10px 5px rgba(153, 153, 153, 0.1);
    display: flex;
    flex-direction: row;
    align-items: center;
}
.describe-box{
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
}
.describe-box span{
    font-size: 16px;
    font-weight: bold;
    color: #3A71A8;
}
.box-card-component {
    .box-card-header {
        position: relative;
        height: 150px;
        img {
            width: 100%;
            height: 100%;
            transition: all 0.2s linear;
            &:hover {
                transform: scale(1.1, 1.1);
                filter: contrast(130%);
            }
        }
    }
}
.avatar-wrapper{
    width: 80px;
    height: 80px;
    margin-left: 10px;
    border-radius: 50%;
    padding: 10px;
    .user-avatar{
        width: 100%;
        height: 100%;
        border-radius: 50%;
    }
}
.card-header{
    width: 100%;
    height: 80px;
    display: flex;
    flex-direction: row;
    align-items: center;
}
.divider1{
    margin-top: 10px;
    width: 90%;
    margin-left: 5%;
    height: 5px;
    border-top-left-radius: 2.5px;
    border-top-right-radius: 2.5px;
    border-bottom-left-radius: 2.5px;
    border-bottom-right-radius: 2.5px;
    background: #4AB7BD;
}
.card-describe{
    width: 100%;
    min-height: 50px;
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: 20px;
}
.record{
    width: 63px;
    height: 63px;
    padding: 10px;
    border-radius: 50%;
    background: #409eff;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
.record img{
    width: 30px;
    height: 30px
}
.record-text{
    word-wrap:break-word;
    word-break:normal; 
}
.record-text span{
    font-weight: 500;
    font-size: 15px;
    color: #409eff;
}
.model-container{
    width: 100%;
    height: 80px;
    padding: 5px;
    border: 1px solid #ccc;
    border-radius: 5px;
    display: flex;
    flex-direction: row;
    margin-bottom: 20px;
}
.img-box{
    width: 30%;
    height: 100%;
}
.img-box img{
    width: 100%;
    height: 100%;
}
.title-box{
    width: 50%;
    height: 100%;
    display: flex;
    flex-direction: row;
    align-items: center;
}
.title-box span{
    font-size: 20px;
    color: #555;
    margin-left: 20px;
    font-weight: bold;
}
.delete-box{
    width: 20%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}
.delete-img{
    width: 20px;
    height: 20px;
    cursor: pointer;
    transition: all ease 0.2s;
}
.delete-img:hover{
    transform: scale(1.1);
}
.delete-img img{
    width: 100%;
    height: 100%;
}
.button-list{
    position: relative;
    width: 100%;
    min-height: 50px;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: flex-end;
    margin-bottom: 10px;
}
