"use strict";

var echarts = require("echarts/index.blank");

require("../coord/mapbox3DCreator");

require("./mapbox3D/Mapbox3DModel");

require("./mapbox3D/Mapbox3DView");

echarts.registerAction({
  type: 'mapbox3DChangeCamera',
  event: 'mapbox3dcamerachanged',
  update: 'mapbox3D:updateCamera'
}, function (payload, ecModel) {
  ecModel.eachComponent({
    mainType: 'mapbox3D',
    query: payload
  }, function (componentModel) {
    componentModel.setMapboxCameraOption(payload);
  });
});